<?php global $ezsql_mysqli_str;$ezsql_mysqli_str=array(1=>'Require $dbuser and $dbpassword to connect to a database server',2=>'Error establishing mySQLi database connection. Correct user/password? Correct hostname? Database server running?',3=>'Require $dbname to SELECT a database',4=>'mySQLi database connection is not active',5=>'Unexpected error while trying to SELECT database');if(!function_exists('mysqli_connect'))die('<b>Fatal Error:</b> ezSQL_mysql requires mySQLi Lib to be compiled and or linked in to the PHP engine');if(!class_exists('ezSQLcore'))die('<b>Fatal Error:</b> ezSQL_mysql requires ezSQLcore (ez_sql_core.php) to be included/loaded before it can be used');class ezSQL_mysqli extends ezSQLcore{var $dbuser=false;var $dbpassword=false;var $dbname=false;var $dbhost=false;var $dbport=false;var $encoding=false;var $rows_affected=false;function ezSQL_mysqli($dbuser='',$dbpassword='',$dbname='',$dbhost='localhost',$encoding=''){$this->dbuser=$dbuser;$this->dbpassword=$dbpassword;$this->dbname=$dbname;list($this->dbhost,$this->dbport)=$this->get_host_port($dbhost,3306);$this->encoding=$encoding;}function quick_connect($dbuser='',$dbpassword='',$dbname='',$dbhost='localhost',$dbport='3306',$encoding=''){$return_val=false;if(!$this->connect($dbuser,$dbpassword,$dbhost,$dbport));else if(!$this->SELECT($dbname,$encoding));else $return_val=true;return $return_val;}function connect($dbuser='',$dbpassword='',$dbhost='localhost',$dbport=false){global $ezsql_mysqli_str;$return_val=false;$this->timer_start('db_connect_time');if(!$dbport){list($dbhost,$dbport)=$this->get_host_port($dbhost,3306);}if(!$dbuser){$this->register_error($ezsql_mysqli_str[1].' in '.__FILE__.' on line '.__LINE__);$this->show_errors?trigger_error($ezsql_mysqli_str[1],E_USER_WARNING):null;}else{$this->dbh=new mysqli($dbhost,$dbuser,$dbpassword,'',$dbport);if($this->dbh->connect_errno){$this->register_error($ezsql_mysqli_str[2].' in '.__FILE__.' on line '.__LINE__);$this->show_errors?trigger_error($ezsql_mysqli_str[2],E_USER_WARNING):null;}else{$this->dbuser=$dbuser;$this->dbpassword=$dbpassword;$this->dbhost=$dbhost;$this->dbport=$dbport;$return_val=true;}}return $return_val;}function SELECT($dbname='',$encoding=''){global $ezsql_mysqli_str;$return_val=false;if(!$dbname){$this->register_error($ezsql_mysqli_str[3].' in '.__FILE__.' on line '.__LINE__);$this->show_errors?trigger_error($ezsql_mysqli_str[3],E_USER_WARNING):null;}else if(!$this->dbh){$this->register_error($ezsql_mysqli_str[4].' in '.__FILE__.' on line '.__LINE__);$this->show_errors?trigger_error($ezsql_mysqli_str[4],E_USER_WARNING):null;}else if(!@$this->dbh->SELECT_db($dbname)){if(!$str=@$this->dbh->error)$str=$ezsql_mysqli_str[5];$this->register_error($str.' in '.__FILE__.' on line '.__LINE__);$this->show_errors?trigger_error($str,E_USER_WARNING):null;}else{$this->dbname=$dbname;if($encoding!=''){$encoding=strtolower(str_replace("-","",$encoding));$charsets=array();$result=$this->dbh->query("SHOW CHARACTER SET");while($row=$result->fetch_array(MYSQLI_ASSOC)){$charsets[]=$row["Charset"];}if(in_array($encoding,$charsets)){$this->dbh->query("SET NAMES '".$encoding."'");}}$return_val=true;}return $return_val;}function escape($str){if(!isset($this->dbh)||!$this->dbh){$this->connect($this->dbuser,$this->dbpassword,$this->dbhost,$this->dbport);$this->SELECT($this->dbname,$this->encoding);}return $this->dbh->escape_string(stripslashes($str));}function sysdate(){return 'NOW()';}function query($query){if($this->num_queries>=500){$this->disconnect();$this->quick_connect($this->dbuser,$this->dbpassword,$this->dbname,$this->dbhost,$this->dbport,$this->encoding);}$return_val=0;$this->flush();$query=trim($query);$this->func_call="\$db->query(\"$query\")";$this->last_query=$query;$this->num_queries++;$this->timer_start($this->num_queries);if($cache=$this->get_cache($query)){$this->timer_update_global($this->num_queries);if($this->use_trace_log){$this->trace_log[]=$this->debug(false);}return $cache;}if(!isset($this->dbh)||!$this->dbh){$this->connect($this->dbuser,$this->dbpassword,$this->dbhost,$this->dbport);$this->SELECT($this->dbname,$this->encoding);if(!isset($this->dbh)||!$this->dbh||$this->dbh->connect_errno)return false;}$this->result=@$this->dbh->query($query);if($str=@$this->dbh->error){$this->register_error($str);$this->show_errors?trigger_error($str,E_USER_WARNING):null;return false;}if(preg_match("/^(insert|delete|update|replace|truncate|drop|create|alter|begin|commit|rollback|set|lock|unlock|call)/i",$query)){$is_insert=true;$this->rows_affected=@$this->dbh->affected_rows;if(preg_match("/^(insert|replace)\s+/i",$query)){$this->insert_id=@$this->dbh->insert_id;}$return_val=$this->rows_affected;}else{$is_insert=false;$i=0;while($i<@$this->result->field_count){$this->col_info[$i]=@$this->result->fetch_field();$i++;}$num_rows=0;while($row=@$this->result->fetch_object()){$this->last_result[$num_rows]=$row;$num_rows++;}@$this->result->free_result();$this->num_rows=$num_rows;$return_val=$this->num_rows;}$this->store_cache($query,$is_insert);$this->trace||$this->debug_all?$this->debug():null;$this->timer_update_global($this->num_queries);if($this->use_trace_log){$this->trace_log[]=$this->debug(false);}return $return_val;}function disconnect(){@$this->dbh->close();}}