<?php include 'database.php';$id=$_POST['id'];$option=$_POST['op'];if($option=='1'){$query="select * from tbl_kelas where id_tingkat =".$id;$result=$connection->query($query);if(!$result){printf("Errormessage: %s\n",$connection->error);}else{echo "<option value=''> Pilih Kelas </option>";while($data=$result->fetch_array(MYSQLI_ASSOC)){echo "<option value='".$data['id_kelas']."'>".$data['nm_kelas']."</option>";}}}else{$query="select * from tbl_rombel where id_kelas =".$id;$result=$connection->query($query);if(!$result){printf("Errormessage: %s\n",$connection->error);}else{echo "<option value=''> Pilih Rombel </option>";while($data=$result->fetch_array(MYSQLI_ASSOC)){echo "<option value='".$data['id_rombel']."'>".$data['nm_rombel']."</option>";}}} ?>