<?php include 'database.php';if(!isset($_GET['searchTerm'])){$json=[];}else{$search=$_GET['searchTerm'];$sql="SELECT id_siswa,nisn, nama_siswa, tbl_siswa.id_rombel, tbl_rombel.nm_rombel, \n  tbl_kelas.nm_kelas, tbl_kelas.id_tingkat, lib_tingkat.tingkat, id_tajaran \n  FROM tbl_siswa, tbl_rombel, tbl_kelas, lib_tingkat \n  WHERE tbl_siswa.id_rombel=tbl_rombel.id_rombel AND tbl_rombel.id_kelas=tbl_kelas.id_kelas \n  AND tbl_kelas.id_tingkat=lib_tingkat.id_tingkat AND nama_siswa LIKE '%".$search."%' LIMIT 10";$result=$connection->query($sql);$json=[];while($row=$result->fetch_assoc()){$nama=$row['nama_siswa'];$nisn=$row['nisn'];$kelas=$row['nm_kelas'];$tingkat=$row['tingkat'];$json[]=['id'=>$row['id_siswa'],'text'=>$nama.' ( NISN : '.$nisn.', Kelas : '.$kelas.' '.$tingkat.' )'];}}echo json_encode($json);